import React, {useState}  from 'react'
import {PostCreate} from './create'
import {PostsList} from './list'



export function PostsComponent(props) {

  const [newPosts, setNewPosts] = useState([]) //create variables newPosts and setNewPosts

  const canPost = props.canPost === "false" ? false : true
  const handleNewPost = (newPost) => {
    let tempNewPosts = [...newPosts] //create variable tempNewPosts and set it to newPosts array
    tempNewPosts.unshift(newPost) //add new element to tempnewposts array, response should bring back actual post

    setNewPosts(tempNewPosts) //change the state of setNewPosts to tempNewPosts
  }



  return <div className={props.className}>
          {canPost === true && <PostCreate didPost={handleNewPost} className='col-12 mb-3' />}
     <PostsList newPosts = {newPosts} {...props} />
  </div>
}
