function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
          const cookie = cookies[i].trim();
          // Does this cookie string begin with the name we want?
          if (cookie.substring(0, name.length + 1) === (name + '=')) {
              cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
              break;
          }
      }
  }
  return cookieValue;
}





function lookup(method, endpoint, callback, data) {
  let jsonData; 
  if (data){ //if data exists
    jsonData = JSON.stringify(data)
  }
  const xhr = new XMLHttpRequest() //used to intereact with servers, retrieve data from a URL without doing a full page refresh
  const url = `http://localhost:8000/api${endpoint}`
  console.log(endpoint)
  xhr.responseType = "json"
  xhr.open(method, url) //Initializes a request.

  xhr.onload = function() { //Fired when an XMLHttpRequest transaction completes successfully.
    callback(xhr.response, xhr.status)
  }
  xhr.onerror = function (e) { //e is the short var reference for event object which will be passed to event handlers.
    console.log(e)
    callback({"message": "The request was an error"}, 400)
  }
  xhr.send(jsonData)
}

export function createPost(newPost, callback) {
  lookup("POST", "/posts/create/", callback, {tweet: newPost})
}

export function loadPosts(callback) {
  lookup("GET", "/posts/", callback)
