import React, {useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';


function loadPosts(callback){ //The purpose of this callback function is to change a piece of the state that is a part of the parent component. 
  const xhr = new XMLHttpRequest()
  const method = 'GET' // "POST"
  const url = "http://localhost:8000/api/posts/"
  xhr.responseType = "json"
  xhr.open(method, url)
  xhr.onload = function() {
    callback(xhr.response, xhr.status)
  }
  xhr.onerror = function (e) {
    console.log(e)
    callback({"message": "The request was an error"}, 400)
  }
  xhr.send()
}

function Post(props) {
  const {Post} = props
  const className = props.className ? props.className : 'col-10 mx-auto col-md-6'
  return <div className={className}>
    <p>{Post.id} - {Post.tweet} </p>
    </div>
}



function App() {
  const [Posts, setPosts] = useState([])

  useEffect(() => {
    const myCallback = (response, status) => {
      console.log(response, status)
      if (status === 200){
        setPosts(response)
      } else {
        alert("There was an error")
      }
    }
    loadPosts(myCallback)
  }, [] )

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <p>
          {Posts.map((Post, index)=>{
            return <li>{Post.tweet}</li>
          })}
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
