import React, {useEffect, useState}  from 'react'

import {apiPostAction, apiPostCreate, apiPostList} from './lookup'
/*
export function PostsComponent(props) {
  const textAreaRef = React.createRef() //React refs make it possible for you to directly access the DOM in React.
  const [newPosts, setNewPosts] = useState([]) 

  const handleBackendUpdate = (response, status) => {
    // backend api response handler
    let tempNewPosts = [...newPosts] //create variable tempNewPosts and set it to newPosts array
    if (status === 201){
      tempNewPosts.unshift(response) //add new element to tempnewposts array, response should bring back actual post
      setNewPosts(tempNewPosts) //change the state of setNewPosts to tempNewPosts

    } else {
      console.log(response)
      alert("An error occured please try again")
    }
  }

  const handleSubmit = (event) => { 
    event.preventDefault() //if intended action does not occur prevent default action
    const newVal = textAreaRef.current.value //set newVal equal to the value of the text area
    // backend api request
    apiPostCreate(newVal, handleBackendUpdate) //newVal is the new post
    //response and status are the callback
    textAreaRef.current.value = '' // set text area to be blank after submit
  }
  return <div className={props.className}>
          <div className='col-12 mb-3'>
            <form onSubmit={handleSubmit}>   
              <textarea ref={textAreaRef} required={true} className='form-control' name='Post'>

              </textarea>
              <button type='submit' className='btn btn-primary my-3'>Post</button>
          </form>
          </div>
      <PostsList newPosts = {newPosts}/>
  </div>
}
*/

export function PostsList(props) {
  const [PostsInit, setPostsInit] = useState([])
  const [Posts, setPosts] = useState([])
  const [PostsDidSet, setPostsDidSet] = useState(false)
  useEffect(()=>{
    const final = [...props.newPosts].concat(PostsInit) //join new posts and posts init
    if (final.length !== Posts.length) {
      setPosts(final) 
    }
  }, [props.newPosts, Posts, PostsInit])

  useEffect(() => {           
    if (PostsDidSet === false){       // if it is false
      const handlePostListLookup  = (response, status) => { //set handlePostListLookup to response and status
        if (status === 200){ 
          setPostsInit(response) //if status is 200 setPostsInit to response
          setPostsDidSet(true) //change from false to true
        } else {
          alert("There was an error")
        }
      }
      apiPostList(handlePostListLookup) //update post list with resposne and status

    }
  }, [PostsInit, PostsDidSet, setPostsDidSet]) 
  return Posts.map((item, index)=>{
    return <Post Post={item} className='my-5 py-5 border bg-white text-dark' key={`${index}-{item.id}`} />
  })
}


export function ActionBtn(props) {
    const {Post, action} = props
    const [likes, setLikes] = useState(Post.likes ? Post.likes : 0)
   // const [userLike, setUserLike] = useState(Post.userLike === true ? true : false)
    const className = props.className ? props.className : 'btn btn-primary btn-sm'
    const actionDisplay = action.display ? action.display : 'Action'

    const handleActionBackendEvent = (response, status) =>{
      console.log(response, status)
      if (status === 200){
        setLikes(response.likes)
        //setUserLike(true)
      }     
    }
    const handleClick = (event) => {
      event.preventDefault()
      apiPostAction(Post.id, action.type, handleActionBackendEvent)
    }
    const display = action.type === 'like' ? `${likes} ${actionDisplay}` : actionDisplay
    return <button className={className} onClick={handleClick}>{display}</button>
  }


export function ParentPost(props){
    const {Post} = props
    return Post.parent ? <div className='row'>
    <div className='col-11 mx-auto p-3 border rounded'>
      <p className='mb-0 text-muted small'>Retweet</p>
      <Post className={' '} Post={Post.parent} />
    </div>
    </div> : null
  }

export function Post(props) { 
    const {Post} = props
    const className = props.className ? props.className : 'col-10 mx-auto col-md-6'
    return <div className={className}>
            <div>
              <p>{Post.id} - {Post.tweet}</p>
              <ParentPost Post={Post} />
            </div>
        <div className='btn btn-group'>
          <ActionBtn Post={Post} action={{type: "like", display:"Likes"}}/>
          <ActionBtn Post={Post} action={{type: "unlike", display:"Unlike"}}/>
          <ActionBtn Post={Post} action={{type: "retweet", display:"Retweet"}}/>
        </div>
    </div>
  }
