import React, {useEffect, useState}  from 'react'

import {loadPosts} from '../lookup'

export function TweetsComponent(props) {
  const textAreaRef = React.createRef()
  const handleSubmit = (event) => {
    event.preventDefault()
    const newVal = textAreaRef.current.value
    console.log(newVal)
    textAreaRef.current.value = ''
  }
  return <div className={props.className}>
          <div className='col-12 mb-3'>
            <form onSubmit={handleSubmit}>
              <textarea ref={textAreaRef} required={true} className='form-control' name='tweet'>

              </textarea>
              <button type='submit' className='btn btn-primary my-3'>Tweet</button>
          </form>
          </div>
      <TweetsList />
  </div>
}





export function PostsList(props) {
    const [Posts, setPosts] = useState([])

    useEffect(() => {
      const myCallback = (response, status) => {
        if (status === 200){
          setPosts(response)
        } else {
          alert("There was an error")
        }
      }
      loadPosts(myCallback)
    }, [])
    return Posts.map((item, index)=>{
      return <Post Post={item} className='my-5 py-5 border bg-white text-dark' key={`${index}-{item.id}`} />
    })
  }


  export function ActionBtn(props) {
    const {Post, action} = props
    const [likes, setLikes] = useState(Post.likes ? Post.likes : 0)
    const [userLike, setUserLike] = useState(Post.userLike === true ? true : false)
    const className = props.className ? props.className : 'btn btn-primary btn-sm'
    const actionDisplay = action.display ? action.display : 'Action'

    const handleClick = (event) => {
      event.preventDefault()
      if (action.type === 'like') {
        if (userLike === true) {
          // perhaps i Unlike it?
          setLikes(likes - 1)
          setUserLike(false)
        } else {
          setLikes(likes + 1)
          setUserLike(true)
        }

      }
    }
    const display = action.type === 'like' ? `${likes} ${actionDisplay}` : actionDisplay
    return <button className={className} onClick={handleClick}>{display}</button>
  }


  export function Post(props) {
    const {Post} = props
    const className = props.className ? props.className : 'col-10 mx-auto col-md-6'
    return <div className={className}>
        <p>{Post.id} - {Post.tweet}</p>
        <div className='btn btn-group'>
          <ActionBtn Post={Post} action={{type: "like", display:"Likes"}}/>
          <ActionBtn Post={Post} action={{type: "unlike", display:"Unlike"}}/>
          <ActionBtn Post={Post} action={{type: "retweet", display:""}}/>
        </div>
    </div>
  }
